import React, { Component } from "react";
import FriendCard from "./components/FriendCard";
import Wrapper from "./components/Wrapper";
import Title from "./components/Title";
import friends from "./friends.json";
import Navbar from "./components/Navbar";

let score = 0;
let topscore = 0;
let clicked = false;

class App extends Component {
  // Setting this.state.friends to the friends json array
  state = {
    friends,
    clicked,
    topscore:0,
    score:0
  };

  handleShuffleCards = id => {
    
     friends.clicked = friends.filter(friend => friend.id === id);
     console.log(id);
     console.log(friend);
     this.setState({ score: score++ });
     console.log(friends.clicked, 'i am friends.clicked')
    // already clicked clicked=true
    if (friends.clicked===true) {

      this.setState({ score: 0 });
     
      // the value of all the cards is reinitialized to false
      for (let i = 0; i < friends.length; i++) {
        friends.clicked = false;
      }
      this.setState({ friends, score, clicked });
    }

    else {
      friends.clicked = true;
      // this.setState({ score: score++ });

      friends.sort(() => Math.random() - 0.5);
      this.setState({ friends, score, clicked });

      //topscore
      if (score > topscore) {
        topscore = score;
        this.setState({ topscore });
      }

    }

  };

  // Map over this.state.friends and render a FriendCard component for each friend object
  render() {
    return (
      <Wrapper>
        <Navbar
          score={this.state.score}
          topscore={this.state.topscore}
        />
        <Title>Pok√©mon-Clicky-Game (Characters List)</Title>
        {this.state.friends.map(friend => (
          <FriendCard
            handleShuffleCards={this.handleShuffleCards}
            id={friend.id}
            key={friend.id}
            name={friend.name}
            image={friend.image}
          />
        ))}
      </Wrapper>
    );
  }
}

export default App;
